import { Component } from '@angular/core';
import { Todo } from '../+state/todo.service';
import { ADDTODO } from '../+state/actions';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { State } from '../+state/reducer';

@Component({
  selector: 'app-todo-app',
  templateUrl: './todo-app.component.html',
  styleUrls: ['./todo-app.component.scss'],
})
export class TodoAppComponent {
  get todoData() {
    return this.todo.select('todoData');
  }

  todos: State[] = [
    { id: 1, todo: 'First', completed: true },
    { id: 2, todo: 'Second', completed: false },
    { id: 3, todo: 'Third', completed: false },
    { id: 4, todo: 'Fourth', completed: true },
  ];
  constructor(private fb: FormBuilder, private todo: Todo) {}

  todoForm: FormGroup;
  ngOnInit(): void {
    this.todoForm = this.fb.group({
      name: ['', Validators.required],
    });
  }

  onSubmit() {
    console.log(this.todoForm.value);
    this.todo.dispatch(ADDTODO);
  }
}
<div class="banner-con">
  <div class="banner">
    <div class="content">
      <h1>TODO</h1>
      <img src="./img/icon-sun.svg" alt />
    </div>
    <div class="todo">
      <form [formGroup]="todoForm" (ngSubmit)="onSubmit()">
        <input
          formControlName="name"
          type="text"
          placeholder="Create a new todo.."
          id="name"
        />
        <button class="pos-abs" type="submit" [disabled]="todoForm.invalid">
          <img src="../../assets/img/icon-check.svg" alt="" />
        </button>
      </form>

      <div class="list">
        <div class="todo-con">
          <div class="todo-li" *ngFor="let todo of todos">
            <ng-container>
              <span class="active-check check">
                <img src="assets/img/icon-check.svg" alt=""
              /></span>
              <p class="completed">{{ todo.todo }}</p>
              <button class="edit">
                <img src="assets/img/edit.png" alt="" />
              </button>
              <button class="close">
                <img src="assets/img/icon-cross.svg" alt="" />
              </button>
            </ng-container>
          </div>
          <div class="hr"></div>
        </div>
      </div>
    </div>
  </div>
</div>
