import { Action } from './actions';

export interface State {
  id: number;
  todo: string;
  completed: boolean;
}

const initialState: State = {
  id: 0,
  todo: '',
  completed: false,
};

// Redux Reducer Function
export const reducer = (state: State, action: Action): State => {
  if (action.type === 'addTodo') {
    const newTodo: State = {
      id: state.id + 1,
      todo: action.payload['todoData'].value.name,
      completed: false, // You might want to set this based on user input or default to false
    };

    return {
      ...newTodo,
      id: state.id + 1,
    };
  }
