import { Action } from './actions';

export interface State {
  id: number;
  todo: string;
  completed: boolean;
}

// Redux Reducer Function
export const reducer = (state: State, action: Action): State => {
  if (action.type === 'addTodo') {
    const newTodo: State = {
      id: state.id + 1,
      name: action.payload.todoData.value.name,
      complete: false, // You might want to set this based on user input or default to false
    };

