export interface Action {
  type: CounterAction;
  payload?: { [key: string]: any };
}

export type CounterAction = 'addTodo' | 'updateTodo' | 'deleteTodo';

export const ADDTODO = (): Action => {
  return { type: 'addTodo' };
};
export const UPDATETODO = (): Action => {
  return { type: 'updateTodo' };
};

export const DELETETODO = (): Action => {
  return { type: 'deleteTodo' };
};
