import { Action } from './actions';

export interface State {
  id: number;
  todo: string;
  completed: boolean;
  todos?: State[];
}

export const initialState: State = {
  id: 0,
  todo: '',
  completed: false,
};
// Redux Reducer Function
export const reducer = (state: State = initialState, action: Action): State => {
  if (action.type === 'addTodo') {
    const newTodo: State = {
      id: state.todos ? state.todos.length + 1 : 1,
      todo: action.payload['todoData'].name,
      completed: false,
    };

    // Update the todos array in the state
    const updatedTodos = state.todos ? [...state.todos, newTodo] : [newTodo];

    return {
      ...state,
      id: state.id + 1,
      todo: '',
      completed: false,
      todos: updatedTodos,
    };
  }
