import { Action } from './actions';

export interface State {
  id: number;
  todo: string;
  completed: boolean;
}

export const initialState: State = {
  id: 0,
  todo: '',
  completed: false,
};
export interface StateWithTodos extends State {
  todos: State[];
}

// Update the initialStateWithTodos object
export const initialStateWithTodos: StateWithTodos = {
  ...initialState,
  todos: [
    { id: 1, todo: 'First', completed: true },
    { id: 2, todo: 'Second', completed: false },
    { id: 3, todo: 'Third', completed: false },
    { id: 4, todo: 'Fourth', completed: true },
  ],
};
// Redux Reducer Function
export const reducer = (state: State, action: Action): State => {
  if (action.type === 'addTodo') {
    const newTodo: State = {
      id: state.todos.length + 1,
      todo: action.payload['todoData'].name,
      completed: false,
    };

    // Update the todos array in the state
    const updatedTodos = [...state.todos, newTodo];

    return {
      ...state,
      id: state.id + 1,
      todo: '',
      completed: false,
      todos: updatedTodos,
    };
  }
