import { Component } from '@angular/core';
import { Todo } from '../+state/todo.service';
import * as CounterActions from '../+state/actions';

@Component({
  selector: 'app-todo-app',
  templateUrl: './todo-app.component.html',
  styleUrls: ['./todo-app.component.scss']
})
export class TodoAppComponent {
  get todoData() {
    return this.todo.select('todoData');
  }

  constructor(private todo: Todo) {}

  todoForm = {
    todo: '',
  };


  submit() {
    console.log(this.todoForm.todo);
    this.todo.dispatch({type : 'addTodo',payload : {todoData : this.todoForm.todo}});
  }
}
