export interface Action {
  type: ActionType;
  payload?: { [key: string]: any };
}

export type ActionType = 'addTodo' | 'updateTodo' | 'deleteTodo';

export interface AddTodoAction extends Action {
  type: 'addTodo';
  payload: { title: string };
}

export interface UpdateTodoAction extends Action {
  type: 'updateTodo';
  payload: { id: number; title: string };
}

export interface DeleteTodoAction extends Action {
  type: 'deleteTodo';
  payload: { id: number };
}

export const addTodo = (title: string): AddTodoAction => {
  return { type: 'addTodo', payload: { title } };
};

export const updateTodo = (id: number, title: string): UpdateTodoAction => {
  return { type: 'updateTodo', payload: { id, title } };
};

export const deleteTodo = (id: number): DeleteTodoAction => {
  return { type: 'deleteTodo', payload: { id } };
};
